<script>import classNames from 'classnames';
import { setContext } from 'svelte';
export let divClass = 'relative overflow-x-auto';
export let striped = false;
export let hoverable = false;
export let noborder = false;
export let shadow = false;
export let color = 'default';
export let tableNoWFull=false;
export let forDashboard=false;
const colors = {
    default: 'text-gray-500 dark:text-gray-400',
    blue: 'text-blue-100 dark:text-blue-100',
    green: 'text-green-100 dark:text-green-100',
    red: 'text-red-100 dark:text-red-100',
    yellow: 'text-yellow-100 dark:text-yellow-100',
    purple: 'text-purple-100 dark:text-purple-100',
    indigo: 'text-indigo-100 dark:text-indigo-100',
    pink: 'text-pink-100 dark:text-pink-100',
    custom: ''
};
$: setContext('striped', striped);
$: setContext('hoverable', hoverable);
$: setContext('noborder', noborder);
$: setContext('color', color);
</script>

{#if forDashboard==false}

<div class={classNames(divClass, shadow && 'shadow-md sm:rounded-lg')}>
{#if tableNoWFull == false}
  <table {...$$restProps} class={classNames('w-full text-left text-sm', colors[color], $$props.class)}>
    <slot />
  </table>
{:else}
  <table {...$$restProps} class={classNames('text-left text-sm', colors[color], $$props.class)}>
    <slot />
  </table>
{/if}

</div>

{:else}
<div class={classNames(shadow && 'shadow-md sm:rounded-lg')} style="width: 100%;overflow-x: hidden;">
{#if tableNoWFull == false}
  <table {...$$restProps} class={classNames('w-full text-left text-sm', colors[color], $$props.class)}>
    <slot />
  </table>
{:else}
  <table {...$$restProps} class={classNames('text-left text-sm', colors[color], $$props.class)} style="width: 100%;max-width: 100%;table-layout: fixed;border-collapse: collapse;">
    <slot />
  </table>
{/if}

</div>

{/if}
